import React, { Component, lazy, Suspense } from 'react'
import { BrowserRouter as Router, Switch, Route, Redirect} from 'react-router-dom'
import Spinner from '../assets/components/spinner/Loading-spinner'

import Layout from './layout'
import Login from './authentication/loginForm'
import Analytics from '../components/analytics'

const routes = [
  {
      path: "/",
      exact: true,
      component: lazy(() => import("./authentication/loginForm")) 
  },
  {
      path: "/app",
      exact: true,
      component: lazy(() => import("./layout")),
      routes: [
        {
          path: "/app/analytics",
          component: lazy(() => import("../components/analytics"))
        },
      ] 
  },

]

const PrivateRoute = ({ component: Component, route, ...rest  }) => (
        <Route {...rest}  render={(props) => (
            rest.loggedIn === true ? <Component  {...props} /> : <Redirect to={{
                pathname: '/',
            }} />
        )} />
)

const LoginRoute = ({ component: Component, ...rest }) => (
    <Route {...rest} render={(props) => (
        rest.loggedIn === false ? <Component {...props} /> : <Redirect to={{
            pathname: '/home'
        }} />
    )} />
)

class NotFound extends Component {
    render(){
        return (
            <Redirect to="/" />
        )
    }
}

export default class Routing extends Component {
                 render() {
                   return (
                     <Router>
                       <Switch>
                         {/* <PrivateRoute
                           path="/home"
                           component={() => (
                             <Layout
                               changeLoginState={this.props.changeLoginState}
                             />
                           )}
                           loggedIn={this.props.loggedIn}
                         /> */}
                            <Suspense fallback={<Spinner />}>
                         {routes.map((route, i) => (
                           <PrivateRoute routes={route.routes} path={route.path} key={i} exact {...route} changeLoginState={this.props.changeLoginState}  loggedIn={this.props.loggedIn} />
                         ))}
                         </Suspense>
                         <LoginRoute
                           exact
                           path="/"
                           component={() => (
                             <Login
                               changeLoginState={this.props.changeLoginState}
                             />
                           )}
                           loggedIn={this.props.loggedIn}
                         />
                         <Route component={NotFound} />
                       </Switch>
                     </Router>
                   );
                 }
               }
