import React, { Component } from 'react';
import { Mutation } from 'react-apollo'
import gql from 'graphql-tag'
import history from '../../components/history'


const LOGIN = gql`
  mutation login($username : String!, $password : String!) {
    lecturerLogin(username : $username, password : $password) {
      token
      user{
        username
      }
    }
 }`;


export default class LoginMutation extends Component {
  render() {
    const { children, changelecturerLoginState } = this.props;
    return (
      <Mutation
        update = {(store, { data: { lecturerLogin } }) => {
            if(lecturerLogin.token) {
                localStorage.setItem('lecjwt', lecturerLogin.token);

                history.push('/lecturer/home')
                history.go()

            }
        }}
      mutation={LOGIN}>
        {(lecturerLogin, { loading, error}) =>
          React.Children.map(children, function(child){
            return React.cloneElement(child, { lecturerLogin, loading, error });
          })
        }
      </Mutation>
    )
  }
}
