import React, { Component } from "react";
import Events from "../Events";
import eventImg from "../../app-assets/images/slider/06.jpg";

import { Carousel } from "react-bootstrap";

class Home extends Component {
  render() {

    const carouselWidth = {
      height: "300px"
    }

    return (
      <div>
        <section id="carousel-crossfade">

        </section>
        <section id="dashboard-ecommerce">
          <div className="row">
            <div className="col-xl-4 col-md-6 col-12 dashboard-greetings">
              <div className="card">
                <div className="card-header">
                  <h3 className="greeting-text">Congratulations John!</h3>
                  <p className="mb-0">Best seller of the month</p>
                </div>
                <div className="card-content">
                  <div className="card-body">
                    <div className="d-flex justify-content-between align-items-end">
                      <div className="dashboard-content-left">
                        <h1 className="text-primary font-large-2 text-bold-500">
                          $89k
                        </h1>
                        <p>You have done 57.6% more sales today.</p>
                        <button type="button" className="btn btn-primary glow">
                          View Sales
                        </button>
                      </div>
                      <div className="dashboard-content-right">
                        <img
                          // src={ecomerceImg}
                          height={220}
                          width={220}
                          className="img-fluid"
                          alt="Dashboard Ecommerce"
                        />
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>

            <Events />
          </div>
        </section>
      </div>
    );
  }
}

export default Home;
