import React, { Component, Suspense } from 'react';
import { BrowserRouter as Router } from 'react-router-dom'

import Navbar from '../components/navs/Navbar'
import Sidebar from '../components/navs/Sidebar'
import Content from './content'
import Spinner from '../assets/components/spinner/Loading-spinner'
import currentUser  from '../components/'

const Login = React.lazy(() => import('./authentication/loginForm'))

class Layout extends Component {
  state = {
    loggedIn: false,
  };

  componentWillMount() {
    const token = localStorage.getItem("jwt");
    if (token) {
      this.setState({ loggedIn: true });
    }
  }

  changeLoginState = (loggedIn) => {
    this.setState({loggedIn})
  }

  render() {
    const dropContentDown = {
      marginTop: "4em",
    };

    return (
      <Router>
        <div className="vertical-layout vertical-menu-modern 2-columns  navbar-sticky footer-static menu-expanded pace-done">
        {this.state.loggedIn ? (  <div>     
          <div className="header-navbar-shadow" />
            <Sidebar />
              <UserConsumer>
                <Navbar />
            </UserConsumer>          
          <div className="content">
            <div className="content-overlay">
              <div className="content-wrapper" style={dropContentDown}>
                <div className="content-header row"></div>
                <div className="content-body">
                      <Content />
                </div>
              </div>
            </div>
          </div>
         </div>
           ) : (
            <Suspense fallback={<Spinner />}>
              <Login changeLoginState={this.changeLoginState} />
            </Suspense>
          )}
        </div>
      </Router>
    );
  }
}

export default Layout
