import React from 'react';
import {withApollo} from 'react-apollo'
import Helmet from 'react-helmet'
import history from './client/components/history'

import Router from './client/views/router'


class App extends React.Component {

  constructor(props){
    super(props);
    this.unsubscribe = props.client.onResetStore(
      () => this.changeLoginState(false)
    )
  }

  state = {
    loggedIn: false,
    adminlogin: false
  };

  componentWillUnmount(){
    this.unsubscribe();
  }

  componentWillMount(){
    const token = localStorage.getItem('jwt');
    const admintoken = localStorage.getItem('admin-jwt')
    if(token){
      this.setState({loggedIn: true})
    }
    if(admintoken){
      this.setState({adminlogin: true})
    }
  }

  changeLoginState = (loggedIn) => {
    this.setState({loggedIn})
  }

  changeAdminLoginState = (adminlogin) => {
    this.setState({adminlogin})
  }

 render(){
  return (
  <div>
    <Helmet>
      <title>Kings University College</title>
      <meta name="description" content="college content" />
    </Helmet>
    <Router history={history} adminlogin={this.state.admin} loggedIn={this.state.loggedIn} changeAdminLoginState={this.changeAdminLoginState} changeLoginState={this.changeLoginState} />
  </div>

  );
 }
}

export default withApollo(App);
