import React from 'react';
import {withApollo} from 'react-apollo'
import Helmet from 'react-helmet'
import history from './client/components/history'

import Router from './client/views/router'


class App extends React.Component {

  constructor(props){
    super(props);
    this.unsubscribe = props.client.onResetStore(
      () => this.changeLoginState(false)
    )
  }

  state = {
    loggedIn: false,
    adminlogin: false,
    lecturerLogin: false
  };

  componentWillUnmount(){
    this.unsubscribe();
  }

  componentWillMount(){
    const token = localStorage.getItem('jwt');
    const admintoken = localStorage.getItem('adminjwt')
    const lectoken = localStorage.getItem('lecjwt')

    if(token){
      this.setState({loggedIn: true})
    }
    // if(admintoken){
    //   this.setState({adminlogin: true})
    // }
    // if(lectoken){
    //   this.setState({lecturerLogin: true})
    // }
  }

  componentWillM

  changeLoginState = (loggedIn) => {
    this.setState({loggedIn})
  }

  changeAdminLoginState = (adminlogin) => {
    this.setState({adminlogin})
  }

  changelecturerLoginState = (lecturerLogin) => {
    this.setState({lecturerLogin})
  }

 render(){
  return (
    <div>
      <Helmet>
        <title>Kings University College</title>
        <meta name="description" content="college content" />
      </Helmet>
      <Router
        history={history}
        adminlogin={this.state.adminlogin}
        loggedIn={this.state.loggedIn}
        lecloggedIn={this.state.lecturerLogin}
        changeAdminLoginState={this.changeAdminLoginState}
        changeLoginState={this.changeLoginState}
        changelecturerLoginState={this.changelecturerLoginState}
      />
    </div>
  );
 }
}

export default withApollo(App);
