import React, { Component } from 'react';
import { Mutation } from 'react-apollo'
import gql from 'graphql-tag'
import history from '../../components/history'


const LOGIN = gql`
  mutation login($username : String!, $password : String!) {
    adminLogin(username : $username, password : $password) {
      token
      user{
        username
      }
    }
 }`;


export default class LoginMutation extends Component {
  render() {
    const { children, changeAdminLoginState } = this.props;
    return (
      <Mutation
        update = {(store, { data: { adminLogin } }) => {
            if(adminLogin.token) {
                localStorage.setItem('adminjwt', adminLogin.token);
                changeAdminLoginState(true);
                history.push('/admin/home')
                history.go()
            }
        }}
      mutation={LOGIN}>
        {(adminLogin, { loading, error}) =>
          React.Children.map(children, function(child){
            return React.cloneElement(child, { adminLogin, loading, error });
          })
        }
      </Mutation>
    )
  }
}
