import React, { Component, Suspense, lazy } from 'react'

import Navbar from './components/navs/topNav'
import Sidebar from './components/navs/Sidebar'
import CurrentUser from '../components/Queries/currentUser'
import { Route, Switch } from 'react-router-dom';
import Spinner from '../assets/components/spinner/Loading-spinner'

const routes = [
    {
        path: "/admin/home",
        main: lazy(() => import("../components/Home")) 
    },
    {
        path: "/admin/analytics",
        main: lazy(() => import("../components/analytics")) 
    },

  ]

export default class AdminLayout extends Component {

  state = {
    adminlogin: false
  }

  componentWillMount(){
    const admintoken = localStorage.getItem('adminjwt')

      if(admintoken){
      this.setState({adminlogin: true})
    }
  }
 changeAdminLoginState = (adminlogin) => {
    this.setState({adminlogin})
  }

    render() {
      const { adminlogin } = this.state 
        const dropContentDown = {
            marginTop: "4em",
          };
        return (
          <CurrentUser>
            <div className="vertical-layout vertical-menu-modern 2-columns  navbar-sticky footer-static menu-expanded pace-done">

              <div className="header-navbar-shadow" />
              <Sidebar />
              <Navbar changeAdminLoginState={this.props.changeAdminLoginState} />
              <div className="content">
                <div className="content-overlay">
                  <div className="content-wrapper" style={dropContentDown}>
                    <div className="content-header row"></div>
                    <div className="content-body">
                      <div>
                        <Switch>
                          <Suspense fallback={<Spinner />}>
                            {routes.map((route, index) => (
                              <Route
                                key={index}
                                path={route.path}
                                children={<route.main />}
                                changeLoginState={this.props.changeAdminLoginState}
                                loggedIn={adminlogin}
                              />
                            ))}
                          </Suspense>
                        </Switch>
                      </div>
                    </div>
                  </div>
                </div>
              </div>           
          </div>
          </CurrentUser>
        )
    }
}
